mapper.select:
select zyf_score.id as id ,zyf_score.score as score ,sys_user.id as studentId ,sys_user.work_no as workNo ,sys_user.realname ,sys_user.realname as studentName ,zyf_kc.id as kcId ,zyf_kc.kc_name as kcName ,zyf_bj.bj_name as bjName
,zyf_score.create_time as createTime from zyf_score, sys_user, zyf_kc, zyf_bj where zyf_score.kc_id = zyf_kc.id and zyf_score.student_id = sys_user.id and zyf_bj.id = sys_user.org_code
and sys_user.work_no = #{query.workNo}
and sys_user.realname like #{query.studentName}
and kc_name like #{query.kcName}
and bj_name like #{query.bjName}
and sys_user.org_code in ( select id from zyf_bj where zyf_bj.zy_id = #{query.zyId} )
order by ${query.column} ${query.order}

set @@sql_mode = 'STRICT_TRANS_TABLES,NO_ZERO_IN_DATE,NO_ZERO_DATE,ERROR_FOR_DIVISION_BY_ZERO,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION';
CREATE OR replace view temp_bj as ( select zyf_bj.bj_name as bjName , sys_user.id as studentId , sys_user.realname as studentName , sys_user.work_no as studentWorkNo , zyf_zy.zy_name as zyName , zyf_zy.id as zyId , zyf_nj.nj_name as njName , zyf_nj.id as njId from sys_user, zyf_bj, zyf_zy, zyf_nj where true and zyf_bj.id = sys_user.org_code and zyf_bj.nj_id = zyf_nj.id and zyf_bj.zy_id = zyf_zy.id and zyf_bj.nj_id != 1 and zyf_bj.zy_id != 1 );
CREATE OR replace view temp_score as ( select sys_user.id as studentId , zyf_score.score , zyf_kc.kc_name as kcName from zyf_score, sys_user, zyf_kc where zyf_score.student_id = sys_user.id and zyf_score.kc_id = zyf_kc.id and zyf_kc.id != 1 );
CREATE OR replace view temp_zc as ( select sys_user.id as studentId , sum(zyf_zc.score) as zcScore from sys_user, zyf_zc, zyf_zc_student where zyf_zc_student.student_id = sys_user.id and zyf_zc_student.zc_id = zyf_zc.id group by studentId );
select studentId, avgScore, zcScore, (CASE WHEN zcScore < 21 THEN 21 WHEN zcScore > 35 THEN 35 ELSE zcScore END) + avgScore * 0.65 as realScore, bjName, zyName, njName, studentName, studentWorkNo from (select temp_score.studentId as studentId , avg(score) as avgScore , IF(temp_zc.zcScore is null, 0, temp_zc.zcScore)+21 zcScore , temp_bj.bjName as bjName , temp_bj.zyName as zyName , temp_bj.njName as njName , temp_bj.studentName as studentName , temp_bj.studentWorkNo as studentWorkNo from temp_score left join temp_zc on temp_zc.studentId = temp_score.studentId join temp_bj on temp_score.studentId = temp_bj.studentId where true
and temp_score.studentId = #{query.studentId}
and temp_bj.zyId = #{query.zyId} and temp_bj.njId = #{query.njId}
group by temp_score.studentId ) a order by realScore desc
